import { useState } from 'react'

import Name from './components/Name'
import Add from './components/Add'
import Filter from './components/Filter'


const App = () => {
  const [persons, setPersons] = useState([
    { 
      content: 'Arto Hellas' ,
      phone: '555-555-5555',
      id: 1,
    }
  ]) 

  const [newName, setNewName] = useState('')
  const [newPhone, setNewPhone] = useState('')
  const [newFilter, setNewFilter] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
    let help = 1
    for (let i = 0; i < persons.length; i++) {
      if (persons[i].content === newName) {
        alert(`${newName} is already added to phonebook`)
        help--
      }
    }
    if(help) {
      const personObject = {
      content: newName,
      phone: newPhone,
      id: persons.length + 1,
    }

    setPersons(persons.concat(personObject))
    }

    setNewName('')
    setNewPhone('')
  }

  const handlePersonChange = (event) => setNewName(event.target.value)
  const handlePhoneChange = (event) => setNewPhone(event.target.value)
  const handleFilterChange = (event) => setNewFilter(event.target.value)

  const filteredPersons = persons.filter((person) => {
    return person.content.toLowerCase().includes(newFilter.toLowerCase())
  })

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter shown with
        <input
          value={newFilter}
          onChange={handleFilterChange}
        />
      </div>
      <Add key={persons.id} persons={persons} setPersons={setPersons} />
      <Name key={filteredPersons.id} filteredPersons={filteredPersons} />
    </div>
  )
}

export default App
